function setCookie(n,t){document.cookie=`${n}=${t}; expires=Sat, 1 Jan 2030 12:00:00 UTC; path=/`}function getCookie(n){for(var t,r=n+"=",f=decodeURIComponent(document.cookie),u=f.split(";"),i=0;i<u.length;i++){for(t=u[i];t.charAt(0)==" ";)t=t.substring(1);if(t.indexOf(r)==0)return t.substring(r.length,t.length)}return""}function loadThemeFromCookie(){let n=getCookie("theme");n=="light"&&(document.getElementById("theme-switch").checked=!0,switchTheme())}function switchTheme(){let n=document.getElementsByTagName("html")[0].classList;document.getElementById("theme-switch").checked?(n.remove("theme-dark"),n.add("theme-light"),setCookie("theme","light")):(n.remove("theme-light"),n.add("theme-dark"),setCookie("theme","dark"))}loadThemeFromCookie();